from django import formsfrom captcha.fields import ReCaptchaField  # ReCaptchaV2class FeedbackCaptchaV2Form(forms.Form):    """    Feedback form with captcha.    """    name = forms.CharField(label='Імʼя', max_length=60,                           widget=forms.TextInput(attrs={'class': 'form-control border'}))    email = forms.EmailField(max_length=60, widget=forms.EmailInput(attrs={'class': 'form-control'}),                             label='Електронна пошта')    subject = forms.CharField(label='Тема', max_length=60,                              widget=forms.TextInput(attrs={'class': 'form-control border'}))    content = forms.CharField(widget=forms.Textarea(attrs={'cols': 30, 'rows': 4, 'class': 'form-control border'}),                              label='Текст')    send_me_copy = forms.BooleanField(label="Відправити мені копію", required=False)    captcha = ReCaptchaField()