from django.urls import path, includefrom equipment_stores.views import EquipmentStoresListView, StoreDetail, EquipmentStoresViewfrom rest_framework import routersrouter = routers.DefaultRouter()router.register('shops', EquipmentStoresView)app_name = 'equipment_stores'urlpatterns = [    path('', EquipmentStoresListView.as_view(), name='main_page'),    path('store/<str:slug>', StoreDetail.as_view(), name='detail'),    path('api/v1/', include(router.urls)),]