from django.db.models.signals import post_savefrom django.dispatch import receiverfrom my_profile.models import Profilefrom registration_authorisation.models import User@receiver(post_save, sender=User)def create_user_profile(sender, instance, created, **kwargs):    """Create User related Profile"""    if created:        Profile.objects.create(user=instance)@receiver(post_save, sender=User)def save_user_profile(sender, instance, **kwargs):    """Save User related Profile"""    instance.profile.save()