from django.urls import pathfrom my_profile import viewsfrom django.contrib.auth import views as auth_viewsurlpatterns = [    path('<str:slug>/', views.UpdateUserDataView.as_view(), name='my_profile'),    path('done/', views.password_change_done, name='password_done'),    path('profile-delete/', views.profile_delete, name='profile_delete'),    path('password_change/',         views.ChangingUserPasswordView.as_view(template_name='my_profile/profile_password_settings.html'),         name='password_change'),    path('reset_password/',         auth_views.PasswordResetView.as_view(template_name='my_profile/password_reset.html'),         name='password_reset'),    path('reset_password_sent/',         auth_views.PasswordResetDoneView.as_view(template_name='my_profile/password_reset_done.html'),         name='password_reset_done'),    path('reset/<uidb64>/<token>/',         auth_views.PasswordResetConfirmView.as_view(             template_name='my_profile/password_reset_confirm.html'), name='password_reset_confirm'),    path('reset_password_complete/',         auth_views.PasswordResetCompleteView.as_view(             template_name='my_profile/password_reset_complete.html'), name='password_reset_complete'),]