from django import formsfrom news_blog.models import Comments, Newsclass CommentsForm(forms.ModelForm):    class Meta:        model = Comments        fields = ['comment_text']        widgets = {            'comment_text': forms.Textarea(attrs={'rows': 4, 'col': 20})        }class AddNewsForm(forms.ModelForm):    class Meta:        model = News        fields = [            'category',            'name', 'description',            'url',            'video', 'news_img'        ]class EditNewsForm(forms.ModelForm):    class Meta:        model = News        fields = [            'name', 'description',            'url',            'video', 'news_img'        ]