import threadingfrom django.db.models.signals import post_savefrom django.dispatch import receiverfrom news_blog.models import Newsfrom news_blog.views import email_mailing@receiver(post_save, sender=News)def users_mailing(sender, instance, created, **kwargs):    if created:        trd_email_mailing = threading.Thread(            target=email_mailing,            args=(instance,),            name='trd_mailing'        )        trd_email_mailing.start()