import threadingfrom django.db.models.signals import post_savefrom django.dispatch import receiverfrom tours.models import Toursfrom telegram_bot.bot import tour_updates_sending@receiver(post_save, sender=Tours)def send_telegram_message(sender, created, instance, **kwargs):    if created:        trd_send_update_to_tg_chat = threading.Thread(            target=tour_updates_sending,            name='tg_chat_update',            args=(instance,)        )        trd_send_update_to_tg_chat.start()