from django import formsfrom tours import modelsclass TourCommentsForm(forms.ModelForm):    class Meta:        model = models.TourComments        fields = ['comment_text']        widgets = {            'comment_text': forms.Textarea(attrs={'rows': 4, 'col': 20})        }class OrganisationCommentsForm(forms.ModelForm):    class Meta:        model = models.OrganisationsComment        fields = ['comment_text']        widgets = {            'comment_text': forms.Textarea(attrs={'rows': 4, 'col': 20})        }class AddTourForm(forms.ModelForm):    class Meta:        model = models.Tours        fields = [            'name', 'organisation', 'description',            'food', 'transfer', 'flying',            'residence', 'price', 'currency',            'start_date', 'finish_date', 'img'        ]class EditTourForm(forms.ModelForm):    class Meta:        model = models.Tours        fields = [            'name', 'description',            'food', 'transfer', 'flying',            'residence', 'price', 'currency',            'start_date', 'finish_date', 'img',            'is_active'        ]class RatingForm(forms.ModelForm):    class Meta:        model = models.Ratings        fields = [            'rating'        ]class TourRatingsForm(forms.ModelForm):    class Meta:        model = models.TourRatings        fields = [            'rating'        ]class OrganisationBookmarkForm(forms.ModelForm):    class Meta:        model = models.Bookmarks        fields = [            'organisation'        ]class TourBookmarkForm(forms.ModelForm):    class Meta:        model = models.TourBookmarks        fields = [            'tour'        ]