from rest_framework import serializersfrom tours.models import Tours, Organisationsclass ToursSerializer(serializers.ModelSerializer):    class Meta:        model = Tours        fields = (            'name', 'organisation', 'description',            'food', 'transfer', 'flying',            'residence', 'price', 'currency',            'start_date', 'finish_date', 'is_active'        )class OrganisationsSerializer(serializers.ModelSerializer):    class Meta:        model = Organisations        fields = (            'name', 'description', 'category',            'pub_date', 'location',            'url', 'phone',  'email'        )