from django.urls import pathfrom tours import viewsapp_name = 'tours'urlpatterns = [    path('all_tours/<slug>', views.OrganisationTourView.as_view(), name='organisation_tours'),    path('', views.ToursFilteredView.as_view(), name='main_page'),    path('organisations/', views.OrganisationsView.as_view(), name='organisations'),    path('organisations/<slug>', views.OrganisationsDetail.as_view(), name='organisation_detail'),    path('rating/', views.RatingView.as_view(), name='rating'),    path('add/', views.AddTourView.as_view(), name='add'),    path('add_bookmark/', views.BookmarkView.as_view(), name='bookmarks'),    path('edit/<slug>', views.EditTourView.as_view(), name='edit'),    path('api/tourslist/', views.ToursAPIView.as_view()),    path('<slug>', views.TourDetail.as_view(), name='detail'),]